import { Empty, Divider, Image, Flex, Card } from 'antd';
import { useContext, useEffect, useState } from 'react';
import { GloablStateContext } from '@/context';
import { HeartTwoTone } from '@ant-design/icons';
import DEMOJSON from '@/assets/demo.json';
import { useTranslation } from '@/i18n/utils';

export default function Layer() {
  const { setReady, setActiveObject, editor } = useContext(GloablStateContext);
  const { t } = useTranslation();



  const loadDemo = async (demo) => {
    // setReady(false);
    await editor.loadFromJSON(demo, true);
    editor.fhistory.reset();
    setReady(true);
    setActiveObject(null);
    editor.fireCustomModifiedEvent();
  }
  // const url = window.location.href;
  const params = new URLSearchParams(window.location.search);

  const [deoms, setDeoms] = useState([])

  const attachments = params.get('attachments');

  console.log('URL parameter:', attachments);

  useEffect(() => {
    const url = attachments;

    async function fetchData() {
      try {
        // Dynamically import node-fetch
        const { default: fetch } = await import('node-fetch');

        const response = await fetch(url);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.text();
        setDeoms([data]);
        console.log('Response:', data);
      } catch (error) {
        console.error('Fetch error:', error);
      }
    }

    // Call the async function to execute the fetch
    if (attachments) {
      fetchData();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);


  return (
    <div
      className="fabritor-panel-wrapper"
    >
      {!deoms ? <Empty
        image={null}
        description={
          <div>
            <HeartTwoTone twoToneColor="#eb2f96" style={{ fontSize: 40 }} />
            <p style={{ color: '#aaa', fontSize: 16 }}>{t('panel.design.start')}</p>
            <Divider />
          </div>
        }
      /> :
        <Flex vertical gap={8} style={{ marginTop: 16 }}>
          {
            deoms.map((demo, index) => (
              <Card
                key={index}
                hoverable
                onClick={() => { loadDemo(demo) }}
                bodyStyle={{
                  padding: '12px 30px'
                }}
              >
                <Image
                  width={200}
                  height={180}
                  preview={false}
                  src={demo.imageDisplay ? demo.imageDisplay : "error"}
                  fallback="data:image/png;base64,"


                />
              </Card>
            ))
          }
        </Flex>
      }

    </div>
  )
}